# Exit on error, unset variable as error, and propagate failure in pipelines
set -euo pipefail

# Function to check if the required tools are installed
requirements() {
    local required_tools=("physlock")
    local missing_tools=()

    for tool in "${required_tools[@]}"; do
        if ! command -v "$tool" &>/dev/null; then
            missing_tools+=("$tool")
        fi
    done

    if [ ${#missing_tools[@]} -gt 0 ]; then
        notify-send -u critical -a "$0" "Missing tools: ${missing_tools[@]}" ||
        echo "Missing tools: ${missing_tools[@]}" 2>&1
        notify-send -u critical -a "$0" "ADJUST LINES 79 & 101" ||
        echo "$0 - ADJUST LINES 79 & 101" 2>&1
        return 1
    else
        return 0
    fi
}

# Main function to handle the lockscreen
main() {
    if requirements; then
        ##_ADJUST_LINE_BELOW_WITH_YOUR_OWN_LOCKSCREEN_TOOL_##
        physlock -ms -p "$message"
    fi
}

# Start of the script
main
